import { createReducer, on } from '@ngrx/store';
import { cargarStarships, cargarStarshipsSuccess, cargarStarshipsError } from '../actions';
export const StarshipsInitialState = {
  starships: null,
  loading: false,
  error: null
};
const _starshipsReducer = createReducer(StarshipsInitialState, on(cargarStarships, state => ({
  ...state,
  loading: true
})), on(cargarStarshipsSuccess, (state, {
  starships
}) => ({
  ...state,
  loading: false,
  starships: {
    ...starships
  }
})), on(cargarStarshipsError, (state, {
  payload
}) => ({
  ...state,
  loading: false,
  error: {
    url: payload.url,
    name: payload.name,
    message: payload.message
  }
})));
export function starshipsReducer(state, action) {
  return _starshipsReducer(state, action);
}
